
public class shopcart {

	int items=0;
	int val=0;
	public int itemscount() {
		// TODO Auto-generated method stub
		
		return items;
	}

	public int cartvalue() {
		// TODO Auto-generated method stub
		return val;
	}

	public void additem() {
		// TODO Auto-generated method stub
		items++;
	}

	public void addval(int i) {
		// TODO Auto-generated method stub
		val+=i;
	}

}
